package day06_operators2;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int employees = 50;
		employees++; // automaticly adding one(1)
		employees++; // add/increment by 1 every time u repeating it
		employees +=1;
		employees = employees + 1; // all options to increase ur var
		employees --; // decrease by 1 (postecrement)
		--employees ; // (preecrement)
		System.out.println(employees);

	}

}
